package six

import "testing"

var (
	input = "\n"
)

func BenchmarkFind(b *testing.B) {
	/*
		goos: darwin
		goarch: amd64
		cpu: Intel(R) Core(TM) i7-9750H CPU @ 2.60GHz
		BenchmarkFind/One-12         	  151975	      9082 ns/op	   16388 B/op	       2 allocs/op
		BenchmarkFind/Two-12         	  549766	      2255 ns/op	       4 B/op	       1 allocs/op
		PASS
		ok  	filepath/aoc2022/six	4.803s
	*/

	b.Run(`One`, func(b *testing.B) {
		var answer string
		var err error
		for n := 0; n < b.N; n++ {
			answer, err = One(input)
			if answer != `1300` {
				b.Fail()
			}
			if err != nil {
				b.Fail()
			}
		}
	})

	b.Run(`Two`, func(b *testing.B) {
		var answer string
		var err error
		for n := 0; n < b.N; n++ {
			answer, err = Two(input)
			if answer != `3986` {
				b.Fail()
			}
			if err != nil {
				b.Fail()
			}
		}
	})
}
